import React, {useState} from 'react';
import TextField from "@material-ui/core/TextField";
import Autocomplete from "@material-ui/lab/Autocomplete";
import logo from './dennis.png';
import './App.css';

function App() {
    return (
        <div className="App">
            <header className="App-header">
                <img src={logo} className="App-logo" alt="logo" />
                <p>
                    <RouteSearch />
                </p>
            </header>
        </div>
    );
}


function RouteSearch() {
    const [shouldShowStopSearch, setShouldShowStopSearch] = useState<boolean>(false);

    return (
        <>
            <Autocomplete
                id="route-search-input"
                disableClearable
                options={busRoutes}
                style={{width: 300}}
                onChange={(event, newValue) => {
                    setShouldShowStopSearch(true)
                }}
                renderInput={(params) => (
                    <TextField
                        {...params}
                        label="Bus Route"
                        margin="normal"
                        variant="outlined"
                        size="medium"
                        InputProps={{
                            ...params.InputProps,
                            type: 'search',
                        }}
                    />
                )}
            />
            {shouldShowStopSearch ? <StopSearch /> : <></>}
        </>
    );
}

function StopSearch() {
    return (
        <Autocomplete
            id="stop-search-input"
            disableClearable
            options={busStops}
            style={{width: 300}}
            onChange={(event, newValue) => {
                console.log(newValue);
            }}
            renderInput={(params) => (
                <TextField
                    {...params}
                    label="Bus Stop"
                    margin="normal"
                    variant="outlined"
                    size="medium"
                    InputProps={{
                        ...params.InputProps,
                        type: 'search',
                    }}
                />
            )}
        />
    );
}

